class Solution:
    def longestValidSubstring(self, word: str, forbidden: list[str]) -> int:
        n = len(word)
        forbidden = set(forbidden)
        
        a = 0
        l = -1
        for r in range(n):
            for k in range(r, max(-1, r - 10), -1):
                s = word[k:r + 1]
                if s in forbidden:
                    l = max(k, l)
                    break
            a = max(a, r - l)

        return a




